CREATE TABLE main.zuvy_coding_questions (
    id integer NOT NULL,
    title character varying(255) NOT NULL,
    description text NOT NULL,
    difficulty character varying(50),
    tag_id integer,
    constraints text,
    created_at timestamp with time zone DEFAULT CURRENT_TIMESTAMP,
    updated_at timestamp with time zone DEFAULT CURRENT_TIMESTAMP,
    usage integer,
    content text,
    version character varying(10),
    embedding jsonb
);

CREATE SEQUENCE main.zuvy_coding_questions_id_seq
    AS integer
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE main.zuvy_coding_questions_id_seq
    OWNED BY main.zuvy_coding_questions.id;

ALTER TABLE ONLY main.zuvy_coding_questions
    ALTER COLUMN id SET DEFAULT nextval('main.zuvy_coding_questions_id_seq'::regclass);



